<script>
import {
  Home,
  FindOne,
  CategoryManagement,
  Cd,
  Voice,
  BroadcastRadio,
  Heartbeat,
  Undo,
  LocalPin,
} from '@icon-park/vue'
export default {
  name: 'index',
  components: {
    Home,
    FindOne,
    CategoryManagement,
    Cd,
    Voice,
    BroadcastRadio,
    Heartbeat,
    Undo,
    LocalPin,
  },
  data() {
    return {
      menus: [
        { path: '/', iconClass: 'Home', text: '首页' },
        { path: '/discovery', iconClass: 'FindOne', text: '发现' },
        { path: '/mv', iconClass: 'CategoryManagement', text: 'MV' },
        { path: '/cd', iconClass: 'Cd', text: '专辑' },
        { path: '/voice', iconClass: 'Voice', text: '歌手' },
        { path: '/broadcast-radio', iconClass: 'BroadcastRadio', text: '电台' },
        { path: '/love', iconClass: 'Heartbeat', text: '最爱' },
        { path: '/local', iconClass: 'LocalPin', text: '本地' },
        { path: '/recent', iconClass: 'Undo', text: '最近' },
      ],
    }
  },
  mounted() {
    this.$anime({
      targets: '#svgGroup path',
      strokeDashoffset: [this.$anime.setDashoffset, 0],
      easing: 'easeInOutSine',
      duration: 1500,
      delay: function (el, i) {
        return i * 250
      },
      direction: 'alternate',
      loop: true,
    })
  },
}
</script>

